import { Component } from '@angular/core';
import { Platform, NavController,ActionSheetController, NavParams, LoadingController, ModalController, ToastController  } from 'ionic-angular';
import { Storage } from '@ionic/storage';
import { ApiServiceProvider } from '../../providers/api-service/api-service';
import { FormBuilder, Validators,FormGroup} from '@angular/forms';
import { IdentityVerificationPage } from '../identity-verification/identity-verification';
import { IdentityVerificationStatusPage } from '../identity-verification-status/identity-verification-status';
import { Camera, CameraOptions } from '@ionic-native/camera';
/**
 * Generated class for the InfoPage page.
 *
 * See http://ionicframework.com/docs/components/#navigation for more info
 * on Ionic pages and navigation.
 */
@Component({
  selector: 'page-info',
  templateUrl: 'info.html',
})


export class InfoPage {

	token:any;
	user:any;
	userDetails:any;
	identityVerificationRequest:any;
	imageSrc:any = "data:image/gif;base64,";
	imageStr:any;
	infoForm: FormGroup;  
	submitAttempt:boolean = false;
	
	
	
	formErrors = {
	  'description': [],
	  'lastName' :[],
	  'firstName' :[],
	  'mobile':[],
	};
	validationMessages = {
	  'description': {
		'required':      'About me is required.',
		'maxLength':     'About me can have maximum 150 characters.',
	  },
	  'lastName': {
		'required':      'Last Name is required.',
	  },
	  'firstName': {
		'required':      'First name is required.',
	  },
	  'mobile': {
		'required':      'Mobile is required.',
	  },
	}
	
	cameraOptions: CameraOptions = {
		quality: 50,
		targetWidth:200,
		targetHeight:200,
		allowEdit:true,
		destinationType: this.camera.DestinationType.DATA_URL,
		encodingType: this.camera.EncodingType.PNG,
		mediaType: this.camera.MediaType.PICTURE
	};
	
	galleryOptions: CameraOptions = {
		quality: 50,
		targetWidth:200,
		targetHeight:200,
		allowEdit:true,
		destinationType: this.camera.DestinationType.DATA_URL,
		encodingType: this.camera.EncodingType.PNG,
		mediaType: this.camera.MediaType.PICTURE,
		sourceType : this.camera.PictureSourceType.PHOTOLIBRARY
	}
	
	constructor(public navCtrl: NavController, 
		public navParams: NavParams, 
		public storage:Storage,
		public fb: FormBuilder, 	  
		public loadingCtrl: LoadingController, 	  
		public apiService: ApiServiceProvider,
		public modalCtrl: ModalController,
		public camera: Camera, 	
		public platform:Platform,
		public actionSheetCtrl: ActionSheetController,
		
		public toastCtrl: ToastController,
	) {
		
		this.infoForm = fb.group({  
			'description': ['', Validators.compose([Validators.required, Validators.maxLength(150)])],
			'firstName': ['', Validators.compose([Validators.required])],
			'lastName': ['', Validators.compose([Validators.required])],
			'mobile': ['', Validators.compose([Validators.required])],
		});
		//let storage = this.storage;
		this.infoForm.valueChanges
		.subscribe(data => this.onValueChanged(data));
		
		
  }

	takePicture() {
		let me = this;
		let actionSheet = this.actionSheetCtrl.create({
		  title: 'Select Picture from',
		  buttons: [
			{
			  text: 'Camera',
			  icon: !this.platform.is('ios') ? 'camera' : null,
			  handler: () => {
				me.camera.getPicture(me.cameraOptions).then((imageData) => {
				// imageData is a base64 encoded string
					me.imageStr = imageData;
					me.updateProfilePicture();
				}, (err) => {
					console.log(err);
				});
			  }
			},{
			  text: 'Gallery',
			  icon: !this.platform.is('ios') ? 'albums' : null,
			  handler: () => {
				me.camera.getPicture(me.galleryOptions).then((imageData) => {
				// imageData is a base64 encoded string
					me.imageStr = imageData;
					me.updateProfilePicture();
				}, (err) => {
					console.log(err);
				});
			  }
			},{
			  text: 'Cancel',
			  role: 'cancel',
			  icon: !this.platform.is('ios') ? 'close' : null,
			  handler: () => {
				console.log('Cancel clicked');
			  }
			}
		  ]
		});
		actionSheet.present();
	}
	
	updateProfilePicture() {
		let toastCtrl = this.toastCtrl;
		let me = this;
		let loadingCtrl = this.loadingCtrl;
		let loading = loadingCtrl.create({
			content: 'Please wait ...'
		});
		
		loading.present();
		me.apiService.updateProfilePicture(me.imageStr, me.token)
			.then(function(data){
				loading.dismiss();
				let response = JSON.parse(JSON.stringify(data));
				if(response.status == "success") 
				{
					
					me.imageSrc = response.data.userDetails.imageUrl;
					let toast = toastCtrl.create({
						message: response.message,
						duration: 3000,
						cssClass: 'toast-success',
						position:'top',
					});
					toast.present();	
				} 
				else 
				{
					let toast = toastCtrl.create({
						message: response.message,
						duration: 3000,
						cssClass: 'toast-error',
						position:'top',
					});
					toast.present();
					
				}
			}, function(error){
				loading.dismiss();
				alert(error);
			});
	}
	
	ionViewDidEnter() {
		let loading = this.loadingCtrl.create({
			content: 'Please wait ...'
		});
		loading.present();
		let me = this;
		this.storage.get('loggedinuser').then(function (user){
			
			me.token = user.whoseme_token;
			me.apiService.getProfileData(me.token).then(function(data){
				loading.dismiss();
				let response = JSON.parse(JSON.stringify(data));
				if(response.status == "success") {
					
					me.user = response.data.user;
					me.userDetails = response.data.userDetails;
					me.identityVerificationRequest = response.data.identityVerificationRequest;
					
					me.infoForm.get("firstName").setValue(me.userDetails.first_name);
					me.infoForm.get("lastName").setValue(me.userDetails.last_name);
					me.infoForm.get("mobile").setValue(me.userDetails.mobile);
					me.infoForm.get("description").setValue(me.userDetails.description);
					me.imageSrc = me.userDetails.imageUrl;
								
				}
			});
		});		
	}
	
	isReadonly() {
		return (this.identityVerificationRequest  && this.identityVerificationRequest.status_id != 3) ? true : false;   //return true/false 
	}
	
	verifyIdentity() {
		this.navCtrl.push(IdentityVerificationPage);
	}
	
	back() {
		this.navCtrl.pop();
	}
	
	
	viewStatus() {
		let modal = this.modalCtrl.create(IdentityVerificationStatusPage, {
			'identityVerificationRequest': this.identityVerificationRequest,
			'userDetails' : this.userDetails
		});
		modal.onDidDismiss(data => {
			if(data){
                this.navCtrl.push(IdentityVerificationPage);
            }  		
		});
		
		modal.present();
	}
	
	submitInfo() {
		this.submitAttempt = true;
		let me = this;
		let toastCtrl = this.toastCtrl;
		if(this.isReadonly()) {
			let toast = toastCtrl.create({
			  message: "You cannot change your personal information while verification request is under review",
			  duration: 3000,
			  cssClass: 'toast-error',
			  position:'top',
			});
			toast.present();
			return;
		}
		if(this.infoForm.valid) {
			let loadingCtrl = this.loadingCtrl;
			let apiService = this.apiService;
			
			let loading = loadingCtrl.create({
				content: 'Please wait ...'
			});
			
			loading.present();
			let formData = me.infoForm.value;
			apiService.savePersonaInfo(formData, me.token)
				.then(function(data){
					loading.dismiss();
					let response = JSON.parse(JSON.stringify(data));
					if(response.status == "success") 
					{
						let toast = toastCtrl.create({
						  message: response.message,
						  duration: 3000,
						  cssClass: 'toast-success',
						  position:'top',
						});
						toast.present();
							
					} 
					else 
					{
						let toast = toastCtrl.create({
						  message: response.message,
						  duration: 3000,
						  cssClass: 'toast-error',
						  position:'top',
						});
						toast.present();
						
					}
				}, function(error){
					loading.dismiss();
					alert(error);
				});
			
		} else {
			const form = this.infoForm;
			for (const field in this.formErrors) {
			// clear previous error message
				this.formErrors[field] = [];
				this.infoForm[field] = '';
				const control = form.get(field);
				if (control && (control.dirty || this.submitAttempt) && !control.valid) {
					const messages = this.validationMessages[field];
					for (const key in control.errors) {
						this.formErrors[field].push(messages[key]);
					}
				}
			}
		}
	}
	
	onValueChanged(data?: any) {
		if (!this.infoForm) { return; }
		const form = this.infoForm;
		for (const field in this.formErrors) {
		// clear previous error message
			this.formErrors[field] = [];
			this.infoForm[field] = '';
			const control = form.get(field);
			if (control && control.dirty && !control.valid) {
				const messages = this.validationMessages[field];
				for (const key in control.errors) {
					this.formErrors[field].push(messages[key]);
				}
			}
		}
	}

}
